graphql.query:
query GetProductWithLimitedSuppliers($productId: ID!, $maxSuppliers: Int) {
  product(id: $productId) {
    id
    name
    price
    suppliers(limit: $maxSuppliers) {
      companyName
      location
    }
  }
}

query GetProductWithFilteredSuppliers($id: ID!, $loc: String) {
  product(id: $id) {
    name
    suppliers(location: $loc) {
      companyName
      location
    }
  }
}

query GetProductWithFilteredSuppliers($id: ID!, $loc: String) {
  product(id: $id) {
    name
    suppliers(location: $loc) {
      companyName
      location
    }
  }
}
