graphql.query:
query GetProductWithFilteredSuppliers($productId: ID!, $loc: String) {
  product(id: $productId) {
    name
    price
    # Passing the location filter to the nested field
    suppliers(location: $loc) {
      companyName
      location
    }
  }
}

query GetLimitedSuppliers($id: ID!, $max: Int) {
  product(id: $id) {
    name
    # Using the limit keyword here
    suppliers(limit: $max) {
      companyName
      location
    }
  }
}
